import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

function App() {
  const [messages, setMessages] = useState([]);
  const [eventCounts, setEventCounts] = useState([]);
  const [systemActive, setSystemActive] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const ws = new WebSocket('ws://engine:8000/ws');

    ws.onopen = () => {
      console.log('Connected to Engine WebSocket');
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        setMessages(prev => [...prev, `Type: ${data.type}, Source: ${data.source}, Content: ${data.content}, Timestamp: ${data.timestamp}`]);

        // Update eventCounts for chart
        setEventCounts(prevCounts => {
          const timestamp = new Date(data.timestamp);
          const timeKey = timestamp.toISOString().slice(0, 16); // up to minute precision
          const existingIndex = prevCounts.findIndex(item => item.time === timeKey);
          if (existingIndex >= 0) {
            const updated = [...prevCounts];
            updated[existingIndex].count += 1;
            return updated;
          } else {
            return [...prevCounts, { time: timeKey, count: 1 }];
          }
        });
      } catch (e) {
        setMessages(prev => [...prev, event.data]);
      }
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    return () => {
      ws.close();
    };
  }, []);

  const toggleSystem = async () => {
    setLoading(true);
    try {
      const newState = !systemActive;
      // Call agent endpoint
      await fetch(`http://localhost:8080/${newState ? 'activate' : 'deactivate'}`, { method: 'POST' });
      // Call engine endpoint
      await fetch(`http://engine:8000/api/engine/${newState ? 'activate' : 'deactivate'}`, { method: 'POST' });
      setSystemActive(newState);
    } catch (error) {
      console.error('Error toggling system:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>SecureShield Dashboard</h1>
      <button
        id="toggleSystemBtn"
        type="button"
        className={`text-white text-sm font-semibold px-4 py-1 rounded ${systemActive ? 'bg-green-600' : 'bg-red-600'}`}
        aria-pressed={systemActive}
        aria-label="Toggle system status"
        onClick={toggleSystem}
        disabled={loading}
      >
        {systemActive ? 'System On' : 'System Off'}
      </button>
      <div>
        <h2>Live Attack Analysis</h2>
        <ul>
          {messages.map((msg, idx) => (
            <li key={idx}>{msg}</li>
          ))}
        </ul>
      </div>
      <div style={{ width: '100%', height: 300 }}>
        <h2>Attack Trend (Events per Minute)</h2>
        <ResponsiveContainer>
          <LineChart data={eventCounts} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="time" />
            <YAxis allowDecimals={false} />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="count" stroke="#8884d8" activeDot={{ r: 8 }} />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

export default App;
